package Lesson_2;

public class Equality_Operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 10;
		int b = 20;
		
		//equal to
		System.out.println(a == b);
		
		//NOT equal to
		System.out.println(a != b);
				
		//Greater than
		System.out.println(a > b);
		
		//Less than
		System.out.println(a < b);
		
		//Greater than or equal to
		System.out.println(a >= b);
		
		//Less than or equal to
		System.out.println(a <= b);

	}

}
