package Lesson_3;

public class DoWhile_loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// to check the difference of while and do while, try to change value of "count" to 11, on dowhile and while
		
		int count = 1;
		
		do {
			System.out.println(count);
			count++;
		}while (count < 11);
		

	}

}
